import soapRequest from 'easy-soap-request';
import { readFileSync } from 'fs';

// usage of module
const getBuyExchangeRate = async (indicator) => {
  const xml = readFileSync('./exchangeRates.xml', 'utf-8');
  const dateToday = '24/02/2020';
  const name = 'Daniel Varela Serrano';
  const email = 'daniel.varela.serrano@gmail.com';
  const token = '6031D1V2ER';
  const url = `https://gee.bccr.fi.cr/Indicadores/Suscripciones/WS/wsindicadoreseconomicos.asmx/ObtenerIndicadoresEconomicosXML?Indicador=${indicator}&FechaInicio=${dateToday}&FechaFinal=${dateToday}&Nombre=${name}&SubNiveles=N&CorreoElectronico=${email}&Token=${token}`;
  const { response } = await soapRequest({ url: url, xml: xml }); // Optional timeout parameter(milliseconds)
  const { headers, body, statusCode } = response;
  console.log(headers);
  console.log(body);
  console.log(statusCode);
};

export default getBuyExchangeRate();
