import React, { useState } from 'react';
import { useCookies } from 'react-cookie';
import { Link, Redirect, useHistory } from 'react-router-dom';
import validate from '../../utils/JWTParser';

const Navbar = () => {

    const history = useHistory();

    const [cookies, setCookie, removeCookie] = useCookies(['JWT']);

    const handleLogout = () => {
        removeCookie('JWT', { path: '/' });
        history.push("/login");
    }

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <Link className="navbar-brand" to="/">PowerBank</Link>
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navItems" aria-controls="navItems" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navItems">
                <div className="navbar-nav ml-auto">
                    <span className="navbar-text mr-4">Hello, </span>
                    <button class="btn btn-outline-secondary" type="button" onClick={handleLogout}>Logout</button>
                </div>
            </div>
        </nav>
    );
}

export default Navbar;
