        }
    }, [cookies]);

    return (
        <div className="wrapper">
            <div id="content">
                <Navbar />
                <div>
                    <h2 className="page-title">Account Information</h2>
                </div>
                <Transfers account={account} />
            </div>
        </div>
    );

}

export default AccountsInfo;
