import React, { useState } from 'react';
import { useCookies } from 'react-cookie';
import { Link, Redirect, useHistory } from 'react-router-dom';
import validate from '../../utils/JWTParser';

const Navbar = () => {

    const history = useHistory();

    const [cookies, setCookie, removeCookie] = useCookies(['JWT']);

    const handleLogout = () => {
        removeCookie('JWT', { path: '/' });
        history.push("/login");
    }
    /** */

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <Link className="navbar-brand" to="/">PowerBank</Link>
                <div className=" ml-auto">
                    <span className="navbar-text mr-4">Hello, </span>
                    <Link class="nav-item nav-link mr-4" type="button" data-toggle="modal" data-target="#logoutModal">Logout</Link>
                </div>
                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navItems" aria-controls="navItems" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
            </nav>

            <div class="modal fade" id="logoutModal" tabindex="-1" role="dialog" aria-labelledby="logoutModalTitle" aria-hidden="true">
                <div class="modal-dialog modal-dialog-centered" role="document">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLongTitle">Logout</h5>
                            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div class="modal-body">
                            Are you sure you want to logout?
                        </div>
                        <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-dismiss="modal" onClick={handleLogout}>Logout</button>
                            <button type="button" class="btn btn-danger" data-dismiss="modal">Cancel</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Navbar;
