import React, { useState, useEffect } from 'react';
import { useCookies } from 'react-cookie';
import { useHistory, Link } from 'react-router-dom';
import validate from '../../utils/JWTParser';
import Navbar from '../../components/Navbar/Navbar';
import './Payments.scss'
import Chart from 'chart.js';
import Axios from 'axios';

const Payments = () => {

    const history = useHistory();

    const [services, setServices] = useState([]);
    const [serviceTypes, setServiceTypes] = useState([]);

    const [cookies] = useCookies(['JWT']);

    if (!validate(cookies.JWT)) {
        history.push("/login");
    }

    useEffect(() => {
        Axios.get(`http://localhost:8081/api/v1/service`, {
            headers: { JWT: cookies.JWT }
        }).then(res => {
            const { data } = res;
            setServices(data);
        }).catch(e => {

        });
        Axios.get(`http://localhost:8081/api/v1/serviceType`, {
            headers: { JWT: cookies.JWT }
        }).then(res => {
            const { data } = res;
            setServiceTypes(data);

        }).catch(e => {

        });

    }, [cookies]);

    var ctx = document.getElementById('myChart');
    var ctx2 = document.getElementById('myChart2');
    var myChart = new Chart(ctx, {
        type: 'doughnut',
        data: {
            labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
            datasets: [{
                label: '# of Transactions',
                data: [12, 19, 3, 5, 2, 3],
                backgroundColor: [
                    'rgba(255, 99, 132, 0.2)',
                    'rgba(54, 162, 235, 0.2)',
                    'rgba(255, 206, 86, 0.2)',
                    'rgba(75, 192, 192, 0.2)',
                    'rgba(153, 102, 255, 0.2)',
                    'rgba(255, 159, 64, 0.2)'
                ],
                borderColor: [
                    'rgba(255, 99, 132, 1)',
                    'rgba(54, 162, 235, 1)',
                    'rgba(255, 206, 86, 1)',
                    'rgba(75, 192, 192, 1)',
                    'rgba(153, 102, 255, 1)',
                    'rgba(255, 159, 64, 1)'
                ],
                borderWidth: 1
            }]
        },
        options: {
            scales: {
                yAxes: [{
                    ticks: {
                        beginAtZero: true
                    }
                }]
            }
        }
    });
    var myChart = new Chart(ctx2, {
        type: 'bar',
        data: {
            labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
            datasets: [{
                label: '# of Votes',
                data: [12, 19, 3, 5, 2, 3],
                backgroundColor: [
                    'rgba(255, 99, 132, 0.2)',
                    'rgba(54, 162, 235, 0.2)',
                    'rgba(255, 206, 86, 0.2)',
                    'rgba(75, 192, 192, 0.2)',
                    'rgba(153, 102, 255, 0.2)',
                    'rgba(255, 159, 64, 0.2)'
                ],
                borderColor: [
                    'rgba(255, 99, 132, 1)',
                    'rgba(54, 162, 235, 1)',
                    'rgba(255, 206, 86, 1)',
                    'rgba(75, 192, 192, 1)',
                    'rgba(153, 102, 255, 1)',
                    'rgba(255, 159, 64, 1)'
                ],
                borderWidth: 1
            }]
        },
        options: {
            scales: {
                yAxes: [{
                    ticks: {
                        beginAtZero: true
                    }
                }]
            }
        }
    });

    return (
        <div class="wrapper">
            <div id="content">
                <Navbar />
                <div>
                    <h2 className="page-title">All Payments</h2>
                </div>
                <div className="block-section container">
                    <div className="block-section-header">
                        <div className="block-section-header-edit">
                            <Link to="/payments/payServices" className="btn btn-outline-secondary">Pay Services</Link>
                        </div>
                        <h3 className="block-section-header-text">Monthly Payments Overview</h3>
                    </div>
                    <div className="row">
                        <div className="col-lg-6 payment-chart">
                            <h4>By Service Type</h4>
                            <canvas id="myChart"></canvas>
                        </div>
                        <div className="col-lg-6 payment-chart">
                            <h4>By Day</h4>
                            <canvas id="myChart2"></canvas>
                        </div>
                    </div>

                </div>

            </div>
        </div>
    );

}

export default Payments;
